import * as z from 'zod'

export const accountParser = z.string().regex(/^[.1-5a-z]{0,12}[.1-5a-j]?$/)

export const authorizationParser = z.object({
    actor     : accountParser,
    permission: z.string()
})
export type Authorization = z.infer<typeof authorizationParser>

export const authorizationsParser = z.array(authorizationParser).min(1)
export type Authorizations = z.infer<typeof authorizationsParser>

export const ExtendedAssetParser = z.object({
    quantity: z.string(),
    contract: accountParser
})
export type ExtendedAsset = z.infer<typeof ExtendedAssetParser>