import * as z from 'zod'

export const otcOrderResponseParser = z.object({
    success       : z.boolean(),
    transaction_id: z.string(),
    errormsg      : z.string().optional()
})
export type OtcOrderResponse = z.infer<typeof otcOrderResponseParser>

export const otcQuoteBodyParser = z.object({
    baseAmount : z.number(),
    baseSymbol : z.string(),
    quoteSymbol: z.string()
})
export type OtcQuoteBody = z.infer<typeof otcQuoteBodyParser>

export const otcQuoteResponseParser = z.object({
    success : z.boolean(),
    token   : z.string(),
    qty     : z.number(),
    errormsg: z.string().optional()
})
export type OtcQuoteResponse = z.infer<typeof otcQuoteResponseParser>