import * as z from 'zod'

export const withdrawHistoryMetalSwapParser = z.object({
    id               : z.string(),
    status           : z.string(),
    amount           : z.string(),
    currency         : z.string(),
    network          : z.string(),
    created          : z.string(),
    withdrawalAddress: z.string(),
    withdrawalHash   : z.string().optional()
})
export type WithdrawHistoryMetalSwap = z.infer<typeof withdrawHistoryMetalSwapParser>

export const withdrawHistoryMetalResponseParser = z.object({
    swaps           : z.array(withdrawHistoryMetalSwapParser),
    page            : z.number(),
    pageSize        : z.number(),
    numberOfElements: z.number(),
    totalPages      : z.number(),
    totalElements   : z.number()
})
export type WithdrawHistoryMetalResponse = z.infer<typeof withdrawHistoryMetalResponseParser>
